// export default Navbar;

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate hook
import logo from '../assets/SWLogo.png';
import './Navbar.css';

const Navbar = ({ setCategory, setSearchResults }) => {
  const [searchInput, setSearchInput] = useState('');
  const navigate = useNavigate(); // Get navigate object from useNavigate hook

  const handleInputChange = (e) => {
    setSearchInput(e.target.value);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const trimmedInput = searchInput.trim();
    if (trimmedInput) {
      setSearchResults(trimmedInput);
      navigate(`/search/${encodeURIComponent(trimmedInput)}`);
    }
  };

  const handleCategoryClick = (category) => {
    setCategory(category);
    navigate(`/`);
  };

  return (
    <nav className="navbar navbar-expand-lg">
      <div className="container-fluid">
        <a className="navbar-brand" href="/">
          <img src={logo} style={{ width: '100px' }} alt="TENGRI NEWS" />
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarTogglerDemo02"
          aria-controls="navbarTogglerDemo02"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <button className="nav-link btn btn-link" onClick={() => handleCategoryClick('general')}>NEWS</button>
            </li>
            <li className="nav-item">
              <button className="nav-link btn btn-link" onClick={() => handleCategoryClick('entertainment')}>LIFE</button>
            </li>
            <li className="nav-item">
              <button className="nav-link btn btn-link" onClick={() => handleCategoryClick('sports')}>SPORT</button>
            </li>
            <li className="nav-item">
              <button className="nav-link btn btn-link" onClick={() => handleCategoryClick('science')}>EDU</button>
            </li>
            <li className="nav-item">
              <button className="nav-link btn btn-link" onClick={() => handleCategoryClick('technology')}>TECHNOLOGY</button>
            </li>
            <li className="nav-item">
              <button className="nav-link btn btn-link" onClick={() => handleCategoryClick('health')}>HEALTH</button>
            </li>
          </ul>
          <form className="d-flex" onSubmit={handleSearch}>
            <input
              id="searchInput"
              className="form-control me-2"
              type="search"
              placeholder="Search"
              aria-label="Search"
              value={searchInput}
              onChange={handleInputChange}
            />
            <button id="search" className="btn btn-outline-success" type="submit">Search</button>
          </form>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
