// pages/Home.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Planet from '../components/Planet';

const Home = () => {
  const [planets, setPlanets] = useState([]);
  const [searchKeyword, setSearchKeyword] = useState('');

  useEffect(() => {
    fetchPlanets();
  }, []);

  const fetchPlanets = async () => {
    try {
      const response = await axios.get('/api/planets');
      setPlanets(response.data);
    } catch (error) {
      console.error('Error fetching planets:', error);
    }
  };

  const searchPlanets = async () => {
    try {
      const response = await axios.get(`/api/planets/search?keyword=${searchKeyword}`);
      setPlanets(response.data);
    } catch (error) {
      console.error('Error searching planets:', error);
    }
  };

  const handleSearchInputChange = (event) => {
    setSearchKeyword(event.target.value);
  };

  return (
    <div>
      <h1>Star Wars Planets Catalog</h1>
      <div>
        <input type="text" value={searchKeyword} onChange={handleSearchInputChange} placeholder="Search by keyword" />
        <button onClick={searchPlanets}>Search</button>
      </div>
      <ul>
        {planets.map((planet) => (
          <li key={planet.name}>
            <Planet {...planet} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
